#!/usr/bin/env node
var shell = require("shelljs");

shell.exec("echo 'init...'");
shell.exec("meteor remove insecure");
shell.exec("meteor remove autopublish");
shell.exec("meteor add accounts-password");
shell.exec("meteor add meteortesting:mocha");
shell.exec("meteor add aldeed:collection2"); 
shell.exec("meteor add aldeed:simple-schema"); 
shell.exec("meteor add mdg:validated-method"); 

shell.exec("echo 'delete file'");
shell.exec("rm -rf client");
shell.exec("rm -rf imports");
shell.exec("rm -rf server");
shell.exec("rm -rf tests");
shell.exec("rm -rf public");
shell.exec("rm -rf package.json");
shell.exec("rm -rf package-lock.json");

shell.exec("git clone https://github.com/shouaya/weget.git .weget_install_tmp");
shell.exec("mv .weget_install_tmp/* ./");
shell.exec("rm -rf .weget_install_tmp");
shell.exec("echo 'npm install'");
shell.exec("meteor npm install");
shell.exec("echo 'weget install success'");